                Equation("Praca, moc, energia", "Energia potencjalna", "$      E_p=mgh$     , $     [kg*m^2/{s^2}=J]$     , $      R_z>>h$      <br> $      E_p$      energia potencjalna, $      m$      masa, $      g↖{→}$      przyśpieszenie grawitacyjne, $      h$      wysokość, $      R_z$      promień ziemi, $      J$      dżul"),
                Equation("Praca, moc, energia", "Energia potencjalna sprężystości", "$      {E_{ps}}={k*x^2}/2$     , $      {[J]}$      <br> $      {E_{ps}}$      energia potencjalna sprężystości, $      {k}$      współczynnik sprężystości $     [N/m]$     , $      {x}$      wychylenie/wydłużenie sprężyny"),
                Equation("Praca, moc, energia", "Zasada zachowania energii", "W izolowanym układzie, całkowita energia mechaniczna nie zmienia sie."),

